sections.css:
$color-torch-red: #FE1135;
$color-bright-gray: #363D4E;
$text: $color-bright-gray;

@font-face {
    font-family: 'Function Pro';
    src: url('#{$root}/fonts/function-pro/FunctionPro-Book-webfont.eot');
    src: url('#{$root}/fonts/function-pro/FunctionPro-Book-webfont.eot?#iefix') format('embedded-opentype'),
