    url('#{$root}/fonts/function-pro/FunctionPro-Book-webfont.ttf') format('truetype'),
    url('#{$root}/fonts/function-pro/FunctionPro-Book-webfont.svg#function_probook') format('svg');
    font-weight: 400;
    font-style: normal;
}

