@font-face {
    font-family: 'Proxima Nova';
    src: url('#{$root}/fonts/proxima-nova/proximanova-regular-webfont.eot');
    src: url('#{$root}/fonts/proxima-nova/proximanova-regular-webfont.eot?#iefix') format('embedded-opentype'),
    url('#{$root}/fonts/proxima-nova/proximanova-regular-webfont.woff2') format('woff2'),
    url('#{$root}/fonts/proxima-nova/proximanova-regular-webfont.woff') format('woff'),
    url('#{$root}/fonts/proxima-nova/proximanova-regular-webfont.ttf') format('truetype'),
    url('#{$root}/fonts/proxima-nova/proximanova-regular-webfont.svg#proxima-nova-regular-webfont') format('svg');
    font-weight: 400;
    font-style: normal;
