@font-face {
    font-family: 'Proxima Nova';
    src: url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.eot');
    src: url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.eot?#iefix') format('embedded-opentype'),
    url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.woff2') format('woff2'),
    url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.woff') format('woff'),
    url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.svg#proxima-nova-bold-webfont') format('svg');
    font-weight: 700;
    font-style: normal;
}

@font-face {
    font-family: 'Stampete';
    src: url('#{$root}/fonts/stampete/stampete.eot');
    src: url('#{$root}/fonts/stampete/stampete.eot?#iefix') format('embedded-opentype'),
    url('#{$root}/fonts/stampete/stampete.woff2') format('woff2'),
    url('#{$root}/fonts/stampete/stampete.woff') format('woff'),
    url('#{$root}/fonts/stampete/stampete.ttf') format('truetype'),
    url('#{$root}/fonts/stampete/stampete.svg#stampete') format('svg');
