.columns {}

.column {}

.is-2-5 {
    width: 20.83%;
}
.is-offset-0-5 {
    margin-left: 4.125%;
}
.child-full-height {
    display: flex;
    > * {
        flex-grow: 1;
    }
}

*+ {
    p,
    pre,
    ul,
    ol,
    dl,
    blockquote {
        margin: $baseline * 0.5 0;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        margin-top: $baseline * 1.5;
    }
}

h1,
h2,
h3,
h4,
h5,
h6 {
    +* {
        margin-top: $baseline * 0.5;
    }
}

.navigation {
    background: #1f2732;
    position: relative;
    > .columns {
        margin: 0;
        padding: 0 ($baseline/2);
        border-bottom: 1px solid cyan;
    }
    .column {
        padding-bottom: 0;
    }
    .product-navigation {
        display: none;
    }
    .hamburger {
        border: 2px solid rgba(white, 0.25);
        border-radius: 2px;
        color: white;
        display: inline-block;
        padding: 0 ($baseline/2);
        line-height: 40px;
        text-transform: uppercase;
        font-family: 'Function Pro';
        font-weight: 700;
        text-decoration: none;
    }
    .icon_down_arrow {
        display: inline-block;
        width: 10px;
        height: 10px;
        margin-left: 0;
    }
    .logo svg {
        height: 46px;
        width: 120px;
    }
    path {
        fill: white;
    }
    ul {
        margin: 0;

        @include tablet {
            margin-left: 2.25rem;
        }
        ul {
            display: none;
            margin-top: 0;
            margin-bottom: 0;
            a {
                border-bottom: 0;
                padding: ($baseline / 2) ($baseline * 2);
            }
        }
    }
    li {
        list-style: none;
        margin-top: 0;

        @include tablet {
            border-bottom: 4px solid transparent;
            display: inline-block;
            padding: 5px 10px 16px 10px;
            margin-right: 10px;
            margin-left: 0;
        }

        a {
            color: white;
            border-bottom: 1px solid rgba(white, 0.1);
            display: flex;
            align-items: center;
            justify-content: space-between;
            padding: ($baseline / 2) $baseline;
            text-transform: none;
            font-family: 'Proxima Nova';
            text-decoration: none;
            white-space: nowrap;

            @include tablet {
                border: 0;
                display: inline;
                padding: 0;
            }
    
            &:hover {
                color: cyan;
            }
        }

        &.active {
            border-bottom-color: cyan;
            a {
                color: cyan;
            }
        }

        &.open {
            background: #171d25;

            @include tablet {
                position: relative;
                transform: translateY(1px);
                z-index: 1;
            }
            a {
                border-color: transparent;
                text-decoration: underline;
                text-decoration-color: rgba(white, 0.5);
                &:hover {
                    color: white;
                    text-decoration-color: rgba(white, 0.5);
                }
            }
            ul {
                display: block;

                a {
                    text-decoration: none; 
                }
                .active {
                    a {
                        color: cyan;
                    }
                }
            }
        }
    }
    .spacer {
        flex-grow: 1;
    }
    .navigation-dropdown {
        position: absolute;
        top: auto;
        left: 0;
        z-index: 1000;
        display: none;
        float: left;
        min-width: 160px;
        padding: 5px 0;
        margin: 2px 0 0;
        list-style: none;
        font-size: 14px;
        background-color: #191e24;
        border: 2px solid #555b62;
        border-radius: 4px;
        box-shadow: 0 6px 12px rgba(0,0,0,0.175);
        background-clip: padding-box;
        padding: 12px 24px;
    }
    .open .navigation-dropdown {
        display: block;
        visibility: visible;
        opacity: 1;
    }
    .page-navigation-outer-container {
        display: flex;
        align-items: flex-end;
    }
    .page-navigation-container {
        display: none;
        
        &.expanded {
            display: flex;
            flex-direction: column;
        }

        @include tablet {
            display: flex;
            flex-direction: column;
        }
    }
    .page-navigation {
        white-space: nowrap;
    }
}

.additional-navigation {
    z-index: 990;
    position: relative;
    background: rgba(0,0,0,0.25);
    margin: 0;
    padding: 0 $baseline;
    font-size: 16px;
    transition: transform 0.2s;
    display: none;
    ul {
        margin-left: 0;
    }
    li {
        padding: 0;
        margin: 0;
        border: 0;
    }
    li a {
        display: inline-block;
        padding: $baseline / 2;
        margin-right: 2em;
    }

    &.open {
        display: block;
    }

    &.right {
        text-align: right;
        li a{
            margin-right: 0;
            margin-left: 2em;
        }
    }

    &.center {
        text-align: center;
        li a {
            margin-right: 1em;
            margin-left: 1em;
        }
    }
}

footer {
