}

a {
    text-decoration: underline;
    text-decoration-skip: ink;
    text-decoration-color: rgba($link, 0.25);
    &:hover {
        text-decoration-color: rgba($link-hover, 0.25);
    }
    &:focus {
        text-decoration-color: rgba($link-focus, 0.25);
    }
    &:active {
        text-decoration-color: rgba($link-active, 0.25);
    }
}

.badge {
    background: grey;
    color: white;
    display: inline-block;
    font-size: $size-6;
    padding: 0 5px;
    text-transform: uppercase;
}

.strap {
    color: cyan;
    line-height: 26px;

    .fa {
        background: cyan;
        color: black;
        margin-right: 1ex;
        border-radius: 50%;
        width: 26px;
        height: 26px;
        line-height: 26px;
        font-size: $size-6;
    }
}

.button {
    background: $color-torch-red;
    border: 2px solid transparent;
    color: #FFF;
    cursor: pointer;
    font-family: 'Function Pro', sans-serif;
    font-size: $size-5;
    font-weight: 700;
    line-height: 1;
    padding: 1rem 2rem;
    text-transform: uppercase;
    transition: background 0.2s, border-color 0.2s, color 0.2s;

    &:hover {
        background: transparent;
        border-color: $color-torch-red;
        color: $color-torch-red;
    }

    &:focus {
        background: transparent;
        border-color: $color-torch-red;
        color: $color-torch-red;
        text-decoration: underline;
        text-decoration-color: rgba($color-torch-red, 0.25);
    }
}

a.button {
    display: inline-block;
    text-decoration: none;
}

.product-button {
    position: relative;
    display: inline-block;
    overflow: hidden;
    text-decoration: none;
    @include transform(rotate(1deg));

    .button {
        background: transparent;
        color: $color-torch-red;
        border: 4px solid $color-torch-red;
        font-weight: 800;
        padding: .5rem 1rem;
    }

    &:after {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 200%;
        height: 200%;
        background: url('#{$root}/images/buttons/splatter-effect.png');
        background-repeat: no-repeat;
        background-size: cover; 
    }

    &:hover {
        .button {
            background: $color-torch-red;
            color: #FFF;
        }
    }

    &:focus {
        .button {
            background: $color-torch-red;
            color: #FFF;
            text-decoration: underline;
            text-decoration-color: rgba(#FFF, 1);
        }
    }

    &.product-button-2 {
        @include transform(rotate(-1deg));

        &:after {
            top: auto;
            bottom: -15px;
            left: 0;
        }
    }

    &.product-button-3 {
        &:after {
            top: 0;
            left: auto;
            right: 0;
        }
    }

    &.product-button-4 {
        @include transform(rotate(-1deg));

        &:after {
            top: auto;
            left: auto;
            bottom: 0;
            right: 0;
        }
    }
}

.icon-button {
    $size: 26px;
    $large: 40px;

    background: transparent;
    border: 0;
    cursor: pointer;
    font-family: 'Function Pro', sans-serif;
    position: relative;
    padding: 0 10px 0 $size+10px;
    line-height: $size;

    &:before {
        background: grey;
        border: 2px solid grey;
        color: white;
        content: 'v';
        display: flex;
        height: $size;
        width: $size;
        position: absolute;
        left: 0;
        top: 50%;
        transform: translateY(-50%);
        align-items: center;
        justify-content: center;
        font-size: 14px;
        transition: background 0.2s, color 0.2s;
        text-indent: 0;
        @extend .fa;
    }

    &.large {
        font-size: $size-5;
        padding-left: $large+10px;
        line-height: $large;

        &:before {
            height: $large;
            width: $large;
            font-size: 24px;
        }
    }

    &.hide-text {
        padding-left: 0;
        font-size: 0;
        width: $size;
        margin-right: 1ex;

        &.large {
            width: $large;
        }
    }

    &.invert {
        background: grey;
        color: white;

        &:before {
            background: white;
            border: 2px solid white;
            color: grey;
        }
    }

    &.fa-info:before {
        @extend .fa-info;
    }

    &.fa-plus:before {
        @extend .fa-plus;
    }

    &.fa-play:before {
        @extend .fa-play;
    }

    &.fa-close:before {
        @extend .fa-window-close;
    }

    &:hover,
    &:focus {
        text-decoration: underline;

        &:before {
            background: transparent;
            border-color: white;
        }
    }
}

.feature-quote {
    display: inline-block;
    text-align: center;

    .icons * {
        display: inline-block;
    }

    .icon-star {
        width: 0.5rem;
        path {
            fill: $color-torch-red;
        }
    }

    p {
        font-family: 'Function Pro', sans-serif;
        font-size: 1.5rem;
        font-style: italic;
        font-weight: 700;
        line-height: 1;
        margin: 0.25rem 0;
    }

    cite {
        font-family: 'Function Pro', sans-serif;
        font-size: 0.75rem;
        font-style: normal;
        line-height: 1;
        opacity: 0.75;
    }

    &.large {
        p {
            font-size: 3rem;
        }

        cite {
            font-size: 1.75rem;
        }

        .icon-star {
            width: 1.5rem;
        }
    }
}

.hero-image {
    img {
        display: block;
        width:100%;
    }
}

.hero-image-w-cta {
    position: relative;

    .image {
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        background-size: cover;
        background-position: 50%;
    }

    .container {
        padding-top: 8rem;
        padding-bottom: 4rem;
    }

    p {
        color: #fff;
        font-family: Stampete;
        font-size: $size-1;
        line-height: $size-1;
        margin-bottom: $size-1 * 2;
        padding: 0;
        -webkit-line-clamp: 6;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
    }
}

.avatar {
    background: cyan;
    border-radius: 50%;
    border: 2px solid transparent;
    color: #000;
    cursor: pointer;
    display: inline-block;
    height: 46px;
    width: 46px;
    line-height: 44px;
    text-align: center;
    text-transform: uppercase;
    font-size: 21px;
    font-weight: 400;
    font-family: 'Function Pro';
}

.search-form {
    display: flex;
    margin-top: $baseline / 8;

    input {
        background: rgba(white,0.1);
        border: 0;
        padding: 9px;
        display: block;
        flex-grow: 1;
        font-size: 16px;
    }

    button {
        @extend .fa;
        @extend .fa-search;
        background: rgba(white,0.1);
        border: 0;
        color: white;
        width: 45px;
    }
}

.spinner {
    border: 1px solid grey;
    border-top-color: transparent;
    border-radius: 50%;
    position: relative;
    animation: spinner-rotate 1s linear infinite;
    margin: 0 auto;
    top: 50%;

    width: $baseline * 2;
    height: $baseline * 2;
    border-width: $baseline / 3;
}

@keyframes spinner-rotate {
    0% {
        @include transform(rotate(0));
    }
    100% {
        @include transform(rotate(360deg));
    }
}

.product-quote {
    padding: $baseline;
    position: relative;

    @include mobile {
        padding: 0;

        &:before {
            position: absolute;
            content: '';
            z-index: 10;
            height: 0;
            width: 0;
            top: 50%;
            @include transform(translateY(-50%));
            border-top: 2rem solid transparent;
            border-bottom: 2rem solid transparent;
            border-left: 1.5rem solid #363D4E;
        }
    }

    .product-quote__content {
        background: url('#{$root}/images/components/crinkled-paper-effect.png');
        background-size: cover;
        background-repeat: no-repeat;
        padding: $baseline ($baseline * 2);

        .product-quote__content-heading {
            font-size: $size-4;
            font-weight: bold;
            margin-bottom: $baseline;

            @include mobile {
                margin-bottom: 0;
            }
        }

        .icons {
            text-align: center;
            margin-bottom: $baseline;
        }

        .icons * {
            display: inline-block;
        }

        .icon-star {
            width: 1rem;
            path {
                fill: $color-torch-red;
            }
        }

        blockquote {
            margin-left: 20px;

            svg {
                fill: #6e7789;
                width: 20px;

                &.icon-quote--open {
                    margin-left: -20px;
                }
            }

            p {
                display: inline;
                font-weight: bold;
                color: #6e7789;
            }
        }

        cite {
            margin-left: 20px;
            font-style: normal;
            font-weight: bold;
            color: #6e7789;
        }
        
        hr {
            width: 50%;
            margin-left: 20px;
            background: #6e7789;
        }
    }
}

.content-tile {
    $padding: 0.75rem;
    $transition-in-speed: 0.2s;
    $transition-out-speed: 0.5s;
    $transition-out-delay: 0.5s;

    overflow: hidden;
    position: relative;

    &:before {
        display: block;
        content: '';
        width: 100%;
    }

    .spinner-container {
        position: absolute;
        left: 50%;
        top: 50%;
        transform: translate(-50%, -50%);
        z-index: 0;
    }

    .buttons {
        margin-top: 0;
        max-height: 0;
        transition: max-height $transition-out-speed, margin-top $transition-out-speed;
        transition-delay: $transition-out-delay;
    }

    .content {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        padding: $padding;
        z-index: 1;
    }

    .image {
        background: lightgrey;
        height: 0;
        padding-bottom: 2 / 3 * 100%;
        &:after {
            background: rgba(0, 0, 0, 0.5);
            content: "";
            display: block;
            position: absolute;
            top: $padding;
            bottom: $padding;
            left: $padding;
            right: $padding;
            opacity: 0;
            transition: opacity 0.52s;
            transition-delay: $transition-out-delay;
            z-index: 1;
        }
    }

    img {
        position: relative;
        transition: opacity 0.2s;
        width: 100%;
        z-index: 1;
    }

    img[data-src] {
        opacity: 0;
    }

    .title {
        background: #fff;
        position: absolute;
        bottom: $padding;
        left: $padding;
        padding-top: 3px;
        padding-right: 6px;
        width: calc(70% - #{$padding});
    }

    .progress {
        background-color: $color-torch-red;
        font-size: 0;
        height: 2px;
        position: absolute;
        top: 0;
        left: 0;
    }

    h1 {
        font-size: $size-5;
        font-weight: 800;
        margin: 0;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
    }
    .subheading {
        overflow: hidden;
        text-transform: uppercase;
        font-size: $size-7;
    }

    .theme {
        background: grey;
        color: #fff;
        text-transform: uppercase;
        position: absolute;
        bottom: $padding;
        right: $padding;
        width: calc(30% - #{$padding});
        text-align: center;
        font-family: 'Function Pro';
        height: 15%;
        display: flex;
        flex-direction: column;
        justify-content: center;
    }

    blockquote {
        color: #fff;
        font-size: $size-7;
        margin: $padding;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
        opacity: 0;
        transition: opacity $transition-out-speed;
        transition-delay: $transition-out-delay;
        cite {
            display: block;
            border-top: 1px solid rgba(255, 255, 255, 0.5);
            font-style: normal;
            text-transform: uppercase;
            margin-top: 5px;
            padding-top: 5px;
        }
    }

    .icon {
        position: absolute;
        right: 0;
        bottom: calc(15% + 1.75rem);
        width: 30%;
        opacity: 0;
        transition: opacity $transition-out-speed;
        transition-delay: $transition-out-delay;

        &:after {
            border-top: 20px solid transparent;
            border-left: 30px solid #fff;
            border-bottom: 20px solid transparent;
            content: "";
            display: block;
            width: 0;
            height: 0;
            margin: 0 auto;
        }
    }

    a {
        color: $text;
        &:hover,
        &:focus {
            blockquote,
            .icon,
            .image:after {
                opacity: 1;
                transition-duration: $transition-in-speed;
                transition-delay: 0s;
            }
            .buttons {
                margin-top: $padding;
                max-height: 50px;
                transition-duration: $transition-in-speed;
                transition-delay: 0s;
            }
        }
    }
}

.content-tile.double {
    $transition-in-speed: 0.2s;
    $transition-out-speed: 0.5s;
    $transition-out-delay: 0.5s;

    margin: 0;

    .image {
        height: auto;
        padding-bottom: 0;
        overflow: hidden;

        img {
            display: block;
            height: 100%;
            width: auto;
            transition: filter $transition-out-speed, transform $transition-out-speed;
            transition-delay: $transition-out-delay;
        }
    }

    .badge {
        background: grey;
        color: white;
        display: inline-block;
        margin: 0 2ex;
        padding: 4px 10px 2px;
        text-transform: uppercase;
        font-size: 0.5rem;
        font-family: 'Function Pro', sans-serif;
        vertical-align: text-top;
    }

    .content {
        background: rgba(0,0,0,0.5);
        margin: 0.75rem;
        line-height: 1.2;
        display: flex;
        flex-direction: column;
        transform: translateY(100%);
        transition: transform $transition-out-speed;
        transition-delay: $transition-out-delay;

        ol {
            counter-reset: content-tile-counter;
            list-style: none;
            margin-left: 0;
            flex-grow: 1;
        }

        li {
            position: relative;

            &:before {
                content: counter(content-tile-counter);
                counter-increment: content-tile-counter;
                background: grey;
                color: white;
                display: inline-block;
                width: 24px;
                height: 24px;
                text-align: center;
                line-height: 24px;
                font-family: 'Function Pro', sans-serif;
                font-weight: 700;
                left: 0;
                top: 4px;
                position: absolute;
            }
        }

        li+li {
            margin-top: $baseline;
        }

        li a {
            color: white;
            display: block;
            margin-left: calc(0.75rem + 24px);
            font-size: $size-5;
            font-weight: 700;
            text-decoration: none;

            .meta {
                font-size: $size-7;
                font-weight: 400;
                text-transform: uppercase;
            }

            .item {
                height: 2.4em;
                -webkit-line-clamp: 2;
                -webkit-box-orient: vertical;
                overflow: hidden;
                display: -webkit-box;
            }

            &:hover {
                text-decoration: underline;
                text-decoration-skip: ink;
                text-decoration-color: rgba(white, 0.5);
            }
        }

        .button {
            color: white;
            text-align: center;
            width: 100%;

            &:hover {
                color: $color-torch-red;
            }

            &:focus {
                color: $color-torch-red;
            }
        }
    }

    &:hover,
    &:focus {
        img {
            filter: blur(10px);
            transform: scale(1.05);
            transition: filter $transition-in-speed, transform $transition-in-speed;
            transition-delay: 0s;
        }
        .content {
            transform: translateY(0);
            transition: transform $transition-in-speed;
            transition-delay: 0s;
        }
    }
}

.tri-hero {
    $rotation: 25deg;
    $scaleFactorNarrowColumn: 1.73;
    $scaleFactorWideColumn: 1.52;
    // Scale calculation - cos() + max{(W/H),(H/W)} * sin()

    .images {
        overflow: hidden;
        height: 548px;
        margin-bottom: 0;

        article {
            transform: rotate($rotation);
            overflow: hidden;
            position: relative;
            transform: rotate($rotation) scale($scaleFactorNarrowColumn);

            &:after {
                content: "";
                background: linear-gradient(to top, black 17%, rgba(0, 0, 255, 0) 45%);
                display: block;
                position: absolute;
                top: 0;
                bottom: 0;
                left: 0;
                right: 0;
                transform: translate(-31%, 4%) rotate(-$rotation) scaleX(2);
            }

            &.is-7 {
                transform: rotate($rotation) scale($scaleFactorWideColumn);
                &:after {
                    background: linear-gradient(to bottom, black 23%, rgba(0, 0, 255, 0) 77%);
                    transform: translate(-3%, -13%) rotate(-$rotation);
                }
            }

            &:last-child {
                transform: translateX(40%) rotate($rotation) scale($scaleFactorNarrowColumn);
            }

            &:hover {
                img {
                    opacity: 0;
                }
            }
        }

        img {
            position: absolute;
            top: 0;
            left: 0;
            transform: translate(-31%, 4%) rotate(-$rotation);
        }

        .is-2-5 img {
            height: 100%;
            max-width: none;
        }
            
        .is-7 img {
            transform: translate(-3%, -13%) rotate(-$rotation);
        }
    }

    .content {
        color: white;
        overflow: hidden;
        height:548px;
        margin-top: -548px;
        margin-bottom: 0;
        position: relative;
 
        .icon-button.fa-play {
            position: absolute;
            bottom: $baseline;
            left: $baseline;

            &:before {
                bottom: 0;
                left: 0;
            }
        }

        .is-5 {
            padding: $baseline;
        }

        .is-3 {
            padding-bottom: $baseline;
            padding-left: 1.25rem;
            padding-right: 1rem;
            display: flex;
            flex-direction: column;
            justify-content: flex-end;
            
            h1 {
                font-size: 2rem;
            }
            p {
                -webkit-line-clamp: 1;
                margin: 0 0 $baseline/2;
            }
        }

        h1 {
            font-weight: 800;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
            display: -webkit-box;
        }
        h2 {
            text-transform: none;
            font-weight: 400;
            font-family: 'Proxima Nova';
            text-decoration: underline;
            font-size: 1.5rem
        }
        p {
            -webkit-line-clamp: 3;
            -webkit-box-orient: vertical;
            overflow: hidden;
            display: -webkit-box;
        }
    }
}

.more-info {
    position: relative;
    overflow: hidden;

    .columns {
        margin: 0;
    }

    .icon-button.fa-close {
        position: absolute;
        top: $baseline;
        right: $baseline;

        &:before {
            background: transparent;
            border: 0;
            color: rgba(255,255,255,0.75);
            font-size: 32px;
        }
    }

    .icon-button.fa-play {
        position: absolute;
        bottom: $baseline;
        right: $baseline;
    }
    
    .content {
        color: #fff;
        padding: $baseline;
        position: relative;
        z-index: 1;

        .columns {
            margin-bottom: $baseline / 2;
        }

        p {
            text-align: justify;
        }

        @include until($tablet) {
            .column {
                padding-left: 0;
                padding-right: 0;
            }
        }
    }

    h1 {
        font-weight: 800;
        margin-bottom: $baseline / 2;
        margin-top: 0;
    }

    dl {
        background: rgba(0,0,0,0.75);
        padding: $baseline / 2;
        @include tablet {
            background: transparent;
            padding: 0;
        }
    }

    dt {
        opacity: 0.75;
    }

    .badge {
        margin-bottom: $baseline / 2;
    }

    .synopsis {
        background: #39434b;
        padding: $baseline / 2;
    }

    .strap {
        margin-top: 0;
    }

    .image {
        position: absolute;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        z-index: 0;

        .column {
            padding: 0;
            background-repeat: no-repeat;
            background-size: cover;
        }

        &:after {
            background: linear-gradient(to bottom, black 5rem, transparent 15rem, transparent 75%, black 90%);
            content: "";
            display: block;
            position: absolute;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;

            @include tablet {
                background: linear-gradient(to right, black 33%, transparent 65%);
            }
        }
    }
}

@mixin transition($value) {
  -webkit-transition: $value;
  -moz-transition: $value;
  -o-transition: $value;
  transition: $value;
}

@mixin transform($value) {
  -webkit-transform: $value;
  -moz-transform: $value;
  -o-transform: $value;
  transform: $value;
}
