    &:hover {
        background: transparent;
        border-color: $color-torch-red;
        color: $color-torch-red;
    }

    &:focus {
        background: transparent;
        border-color: $color-torch-red;
        color: $color-torch-red;
        text-decoration: underline;
        text-decoration-color: rgba($color-torch-red, 0.25);
    }
}

.product-button {
    position: relative;
    display: inline-block;
    overflow: hidden;
    text-decoration: none;
    @include transform(rotate(1deg));

    .button {
        background: transparent;
        color: $color-torch-red;
        border: 4px solid $color-torch-red;
        font-weight: 800;
        padding: .5rem 1rem;
    }

    &:after {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 200%;
        height: 200%;
        background: url('#{$root}/images/buttons/splatter-effect.png');
        background-repeat: no-repeat;
        background-size: cover; 
    }

    &:hover {
        .button {
            background: $color-torch-red;
            color: #FFF;
        }
    }

    &:focus {
        .button {
            background: $color-torch-red;
            color: #FFF;
            text-decoration: underline;
            text-decoration-color: rgba(#FFF, 1);
        }
    }

    &.product-button-2 {
        @include transform(rotate(-1deg));

        &:after {
            top: auto;
            bottom: -15px;
            left: 0;
        }
    }

    &.product-button-3 {
        &:after {
            top: 0;
            left: auto;
            right: 0;
        }
    }

    &.product-button-4 {
        @include transform(rotate(-1deg));

        &:after {
            top: auto;
            left: auto;
            bottom: 0;
            right: 0;
        }
    }
}

.feature-quote {
    display: inline-block;
    text-align: center;

    .icons * {
        display: inline-block;
    }

    .icon-star {
        width: 0.5rem;
        path {
            fill: $color-torch-red;
        }
    }

    p {
        font-family: 'Function Pro', sans-serif;
        font-size: 1.5rem;
        font-style: italic;
        font-weight: 700;
        line-height: 1;
        margin: 0.25rem 0;
    }

    cite {
        font-family: 'Function Pro', sans-serif;
        font-size: 0.75rem;
        font-style: normal;
        line-height: 1;
        opacity: 0.75;
    }

    &.large {
        p {
            font-size: 3rem;
        }

        cite {
            font-size: 1.75rem;
        }

        .icon-star {
            width: 1.5rem;
        }
    }
}

.content-tile {
    $padding: 0.75rem;

    position: relative;

    &:before {
        display: block;
        content: '';
        width: 100%;
    }

    .content {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        padding: $padding;
    }

    .image {
        height: 0;
        padding-bottom: 2 / 3 * 100%;
        &:after {
            background: rgba(0, 0, 0, 0.5);
            content: "";
            display: block;
            position: absolute;
            top: $padding;
            bottom: $padding;
            left: $padding;
            right: $padding;
            opacity: 0;
            transition: opacity 0.2s;
        }
    }
    img {
        width: 100%;
    }

    .title {
        background: #fff;
        position: absolute;
        bottom: $padding;
        left: $padding;
        padding-top: 3px;
        padding-right: 6px;
        width: calc(70% - #{$padding});
    }
    h1 {
        font-size: $size-5;
        font-weight: 800;
        margin: 0;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
    }
    .subheading {
        overflow: hidden;
        text-transform: uppercase;
        font-size: $size-7;
    }

    .theme {
        background: grey;
        color: #fff;
        text-transform: uppercase;
        position: absolute;
        bottom: $padding;
        right: $padding;
        width: calc(30% - #{$padding});
        text-align: center;
        font-family: 'Function Pro';
        height: 15%;
        display: flex;
        flex-direction: column;
        justify-content: center;
    }

    blockquote {
        color: #fff;
        font-size: $size-7;
        margin: $padding;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
        opacity: 0;
        transition: opacity 0.2s;
        cite {
            display: block;
            border-top: 1px solid rgba(255, 255, 255, 0.5);
            font-style: normal;
            text-transform: uppercase;
            margin-top: 5px;
            padding-top: 5px;
        }
    }

    button {
        background: gray;
        border: 0;
        color: #fff;
        width: 32px;
        height: 32px;
        margin-right: 1ex;
    }

    .icon {
        position: absolute;
        right: 0;
        bottom: calc(15% + 1.75rem);
        width: 30%;
        opacity: 0;
        transition: opacity 0.2s;

        &:after {
            border-top: 20px solid transparent;
            border-left: 30px solid #fff;
            border-bottom: 20px solid transparent;
            content: "";
            display: block;
            width: 0;
            height: 0;
            margin: 0 auto;
        }
    }

    a {
        color: $text;
        &:hover,
        &:focus {
            blockquote,
            .icon,
            .image:after {
                opacity: 1;
            }
        }
    }
}

.hero-image-w-cta {
    position: relative;

    .image {
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        background-size: cover;
        background-position: 50%;
    }

    .container {
        padding-top: 8rem;
        padding-bottom: 4rem;
    }

    p {
        color: #fff;
        font-family: Stampete;
        font-size: $size-1;
        line-height: $size-1;
        margin-bottom: $size-1 * 2;
        padding: 0;
        -webkit-line-clamp: 6;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
    }
}

.navigation {
    background: rgba(0, 0, 0, 0.5);
    padding: 0.75rem;
    position: relative;

    svg {
        display: block;
        height: 44px;

        path {
            fill: #fff;
        }
    }

    ul {
        list-style: none;
        margin: 0;
    }

    li {
        display: inline-block;
        margin: 0 1.5rem;
        font-size: $size-6;
    }

    a {
        color: #fff;
        font-family: 'Function Pro', sans-serif;
        line-height: 44px;
        text-decoration: none;
        text-transform: uppercase;
    }

    .user {
        background: $color-torch-red;
        display: block;
        height: 44px;
        width: 44px;
        font-size: $size-7;
        font-weight: 700;
        text-align: center;
        position: absolute;
        top: 50%;
        right: 25px;
        @include transform(translateY(-50%));
    }

    @media (max-width: 800px) {
        .navigation-desktop {
            display: none;
        }
        .navigation-mobile {
            display: block;
        }
    }

    @media (min-width: 799px) {
        .navigation-desktop {
            display: block;
        }
        .navigation-mobile {
            display: none;
        }
    }

    .navigation-mobile {
        &.open {
            .navigation-mobile--dropdown {
                display: block;
                z-index: 1;
            }

            .menuButton span {
                background: transparent;
                &:before,
                &:after {
                    @include transition(top .2s 0s);
                    @include transition(bottom .2s 0s);
                    @include transition(transform .2s .2s);
                }
                &:before {
                    top: 0;
                    @include transform(rotate(45deg));
                }
                &:after {
                    bottom: 0;
                    @include transform(rotate(-45deg));
                }
            }
        }
        &:hover {
            .menuButton {
                span {
                    transform: scale(1.2);
                }
            }
        }
    }

    .menuButton {
        position: absolute;
        cursor: pointer;
        width: 30px;
        height: 30px;
        min-width: 10mm;
        min-height: 10mm;
        display: block;
        left: 165px;
        top: 50%;
        @include transform(translateY(-50%));

        span {
            display: block;
            position: absolute;
            top: 18px;
            left: 0;
            right: 0;
            height: 4px;
            background: #fff;
            @include transition(transform .2s ease-in-out);
            &:before,
            &:after {
                content: "";
                position: absolute;
                display: block;
                left: 0;
                right: 0;
                height: 4px;
                background: #fff;
            }
            &:before {
                top: -10px;
            }
            &:after {
                bottom: -10px;
            }
        }
    }

    .navigation-mobile--dropdown {
        position: absolute;
        border: none;
        border-radius: 5px;
        border-top-left-radius: 0;
        border-top-right-radius: 0;
        margin-top: 0px;
        width: 200px;
        background-color: rgba(0, 0, 0, 0.5);
        padding: .75rem;
        width: auto;
        a {
            display: block;
        }
        hr {
            border-top: 1px solid #353f4a;
        }
        @media (max-width: 800px) {
            top: 68px;
            left: 150px;
            z-index: -1;
            a {
                font-size: 20px;
            }
        }
    }
}
