*+ {
    p,
    pre,
    ul,
    ol,
    dl,
    blockquote {
        margin: $baseline * 0.5 0;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        margin-top: $baseline * 1.5;
    }
}

h1,
h2,
h3,
h4,
h5,
h6 {
    +* {
        margin-top: $baseline * 0.5;
    }
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: 'Function Pro';
    font-weight: 700;
    line-height: 1;
    text-transform: uppercase;
}

h1 {
    font-size: $size-1;
}

h2 {
    font-size: $size-2;
}

h3 {
    font-size: $size-3;
}

h4 {
    font-size: $size-4;
}

h5 {
    font-size: $size-5;
}

h6 {
    font-size: $size-6;
}

p {
    line-height: $baseline;
}

ul,
ol {
    list-style-position: outside;
    margin-left: $baseline * 1.5;
}

ul {
    list-style-type: disc;
}

li+li {
    margin-top: $baseline * 0.25;
}

dd+dt {
    margin-top: $baseline * 0.25;
}

pre {
    padding: $baseline 0;
    line-height: $baseline;
}

a {
    text-decoration: underline;
    text-decoration-skip: ink;
    text-decoration-color: rgba($link, 0.25);
    &:hover {
        text-decoration-color: rgba($link-hover, 0.25);
    }
    &:focus {
        text-decoration-color: rgba($link-focus, 0.25);
    }
    &:active {
        text-decoration-color: rgba($link-active, 0.25);
    }
}

.button {
    background: $color-torch-red;
    border: 2px solid transparent;
    color: #FFF;
    cursor: pointer;
    font-family: 'Function Pro', sans-serif;
    font-size: $size-5;
    font-weight: 700;
    line-height: 1;
    padding: 1rem 2rem;
    text-transform: uppercase;
    transition: background 0.2s, border-color 0.2s, color 0.2s;

    &:hover {
        background: transparent;
        border-color: $color-torch-red;
        color: $color-torch-red;
    }

    &:focus {
        background: transparent;
        border-color: $color-torch-red;
        color: $color-torch-red;
        text-decoration: underline;
        text-decoration-color: rgba($color-torch-red, 0.25);
    }
}

a.button {
    display: inline-block;
    text-decoration: none;
}

.product-button {
    position: relative;
    display: inline-block;
    overflow: hidden;
    text-decoration: none;
    @include transform(rotate(1deg));

    .button {
        background: transparent;
        color: $color-torch-red;
        border: 4px solid $color-torch-red;
        font-weight: 800;
        padding: .5rem 1rem;
    }

    &:after {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 200%;
        height: 200%;
        background: url('#{$root}/images/buttons/splatter-effect.png');
        background-repeat: no-repeat;
        background-size: cover; 
    }

    &:hover {
        .button {
            background: $color-torch-red;
            color: #FFF;
        }
    }

    &:focus {
        .button {
            background: $color-torch-red;
            color: #FFF;
            text-decoration: underline;
            text-decoration-color: rgba(#FFF, 1);
        }
    }

    &.product-button-2 {
        @include transform(rotate(-1deg));

        &:after {
            top: auto;
            bottom: -15px;
            left: 0;
        }
    }

    &.product-button-3 {
        &:after {
            top: 0;
            left: auto;
            right: 0;
        }
    }

    &.product-button-4 {
        @include transform(rotate(-1deg));

        &:after {
            top: auto;
            left: auto;
            bottom: 0;
            right: 0;
        }
    }
}

.icon-button {
    $size: 26px;

    background: transparent;
    border: 0;
    cursor: pointer;
    font-family: 'Proxima Nova', sans-serif;
    position: relative;
    padding-left: $size+10px;
    line-height: $size;

    &.hide-text {
        padding-left: 0;
        font-size: 0;
        width: $size;
        margin-right: 1ex;
    }

    &:before {
        background: grey;
        border: 2px solid grey;
        color: white;
        content: '';
        display: flex;
        height: $size;
        width: $size;
        position: absolute;
        left: 0;
        top: 50%;
        transform: translateY(-50%);
        align-items: center;
        justify-content: center;
        font-size: 14px;
        transition: background 0.2s, color 0.2s;
        text-indent: 0;
        @extend .fa;
    }

    &.fa-info:before {
        @extend .fa-info;
    }

    &.fa-plus:before {
        @extend .fa-plus;
    }

    &:hover,
    &:focus {
        text-decoration: underline;

        &:before {
            background: white;
            color: grey;
        }
    }
}

.play-button {
    max-width: 130px;

    &:hover {
        background: #38444C;

        .play-button__icon {
            svg {
                max-height: 30px;
            }
        }
    }
    .play-button__icon {
        width: 34%;
        height: 35px;
        background: white;
        display: inline-block;
        float: left;
        position: relative;

        svg {
            max-height: 25px;
            position: absolute;
            top: 50%;
            left: 50%;
            @include transform(translate(-50%, -50%));
            @include transition(max-height 150ms ease-in);

            polygon {
                fill: #38444C;
            }
        }
    }
    .play-button__text {
        color: white;
        width: 66%;
        height: 35px;
        background: #38444C;
        display: inline-block;
        float: right;
        position: relative;

        p {
            position: absolute;
            top: 50%;
            left: 50%;
            @include transform(translate(-50%, -50%));
            font-weight: 700;
        }
    }
}

.feature-quote {
    display: inline-block;
    text-align: center;

    .icons * {
        display: inline-block;
    }

    .icon-star {
        width: 0.5rem;
        path {
            fill: $color-torch-red;
        }
    }

    p {
        font-family: 'Function Pro', sans-serif;
        font-size: 1.5rem;
        font-style: italic;
        font-weight: 700;
        line-height: 1;
        margin: 0.25rem 0;
    }

    cite {
        font-family: 'Function Pro', sans-serif;
        font-size: 0.75rem;
        font-style: normal;
        line-height: 1;
        opacity: 0.75;
    }

    &.large {
        p {
            font-size: 3rem;
        }

        cite {
            font-size: 1.75rem;
        }

        .icon-star {
            width: 1.5rem;
        }
    }
}

.content-tile {
    $padding: 0.75rem;
    $transition-in-speed: 0.2s;
    $transition-out-speed: 0.5s;
    $transition-out-delay: 0.5s;

    overflow: hidden;
    position: relative;

    &:before {
        display: block;
        content: '';
        width: 100%;
    }

    .buttons {
        margin-top: 0;
        max-height: 0;
        transition: max-height $transition-out-speed, margin-top $transition-out-speed;
        transition-delay: $transition-out-delay;
    }

    .content {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        padding: $padding;
    }

    .image {
        height: 0;
        padding-bottom: 2 / 3 * 100%;
        &:after {
            background: rgba(0, 0, 0, 0.5);
            content: "";
            display: block;
            position: absolute;
            top: $padding;
            bottom: $padding;
            left: $padding;
            right: $padding;
            opacity: 0;
            transition: opacity 0.52s;
            transition-delay: $transition-out-delay;
        }
    }
    img {
        width: 100%;
    }

    .title {
        background: #fff;
        position: absolute;
        bottom: $padding;
        left: $padding;
        padding-top: 3px;
        padding-right: 6px;
        width: calc(70% - #{$padding});
    }
    h1 {
        font-size: $size-5;
        font-weight: 800;
        margin: 0;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
    }
    .subheading {
        overflow: hidden;
        text-transform: uppercase;
        font-size: $size-7;
    }

    .theme {
        background: grey;
        color: #fff;
        text-transform: uppercase;
        position: absolute;
        bottom: $padding;
        right: $padding;
        width: calc(30% - #{$padding});
        text-align: center;
        font-family: 'Function Pro';
        height: 15%;
        display: flex;
        flex-direction: column;
        justify-content: center;
    }

    blockquote {
        color: #fff;
        font-size: $size-7;
        margin: $padding;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
        opacity: 0;
        transition: opacity $transition-out-speed;
        transition-delay: $transition-out-delay;
        cite {
            display: block;
            border-top: 1px solid rgba(255, 255, 255, 0.5);
            font-style: normal;
            text-transform: uppercase;
            margin-top: 5px;
            padding-top: 5px;
        }
    }

    .icon {
        position: absolute;
        right: 0;
        bottom: calc(15% + 1.75rem);
        width: 30%;
        opacity: 0;
        transition: opacity $transition-out-speed;
        transition-delay: $transition-out-delay;

        &:after {
            border-top: 20px solid transparent;
            border-left: 30px solid #fff;
            border-bottom: 20px solid transparent;
            content: "";
            display: block;
            width: 0;
            height: 0;
            margin: 0 auto;
        }
    }

    a {
        color: $text;
        &:hover,
        &:focus {
            blockquote,
            .icon,
            .image:after {
                opacity: 1;
                transition-duration: $transition-in-speed;
                transition-delay: 0s;
            }
            .buttons {
                margin-top: $padding;
                max-height: 50px;
                transition-duration: $transition-in-speed;
                transition-delay: 0s;
            }
        }
    }
}

.content-tile.double {
    $transition-in-speed: 0.2s;
    $transition-out-speed: 0.5s;
    $transition-out-delay: 0.5s;

    margin: 0;

    .image {
        height: auto;
        padding-bottom: 0;
        img {
            display: block;
            height: 100%;
            width: auto;
        }
    }

    .badge {
        background: grey;
        color: white;
        display: inline-block;
        margin: 0 2ex;
        padding: 4px 10px 2px;
        text-transform: uppercase;
        font-size: 0.5rem;
        font-family: 'Function Pro', sans-serif;
        vertical-align: text-top;
    }

    .content {
        background: rgba(0,0,0,0.5);
        margin: 0.75rem;
        line-height: 1.2;
        display: flex;
        flex-direction: column;
        transform: translateY(100%);
        transition: transform $transition-out-speed;
        transition-delay: $transition-out-delay;

        ol {
            counter-reset: content-tile-counter;
            list-style: none;
            margin-left: 0;
            flex-grow: 1;
        }

        li {
            position: relative;

            &:before {
                content: counter(content-tile-counter);
                counter-increment: content-tile-counter;
                background: grey;
                color: white;
                display: inline-block;
                width: 24px;
                height: 24px;
                text-align: center;
                line-height: 24px;
                font-family: 'Function Pro', sans-serif;
                font-weight: 700;
                left: 0;
                top: 4px;
                position: absolute;
            }
        }

        li+li {
            margin-top: $baseline;
        }

        li a {
            color: white;
            display: block;
            margin-left: calc(0.75rem + 24px);
            font-size: $size-5;
            font-weight: 700;
            text-decoration: none;

            .meta {
                font-size: $size-7;
                font-weight: 400;
                text-transform: uppercase;
            }

            .item {
                height: 2.4em;
                -webkit-line-clamp: 2;
                -webkit-box-orient: vertical;
                overflow: hidden;
                display: -webkit-box;
            }

            &:hover {
                text-decoration: underline;
                text-decoration-skip: ink;
                text-decoration-color: rgba(white, 0.5);
            }
        }

        .button {
            color: white;
            text-align: center;
            width: 100%;

            &:hover {
                color: $color-torch-red;
            }

            &:focus {
                color: $color-torch-red;
            }
        }
    }

    &:hover,
    &:focus {
        .content {
            transform: translateY(0);
            transition: transform $transition-in-speed;
            transition-delay: 0s;
        }
    }
}

