.tri-hero {
    position: relative;

    .play-button {
        position: absolute;
        bottom: 1rem;
        left: 1rem;
        width: 110px;
    }
    .hero {
        color: white;
        display: inline-block;
        padding: 1rem;
        height: 350px;
    }
    .hero-copy {
        width: 92%;
        z-index: 4;
        position: relative;
    }
    .hero-image {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;

        svg {
            height: 100%;
        }

        &:before {
            content: '';
            position: absolute;
            top: 0;
            right: 0;
            bottom: 0;
            left: 0;
            background: -moz-linear-gradient(left,  rgba(0,0,0,1) 0%, rgba(0,0,0,0) 100%); /* FF3.6-15 */
            background: -webkit-linear-gradient(left,  rgba(0,0,0,1) 0%,rgba(0,0,0,0) 100%); /* Chrome10-25,Safari5.1-6 */
            background: linear-gradient(to right,  rgba(0,0,0,1) 0%,rgba(0,0,0,0) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
            filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#cc000000', endColorstr='#007db9e8',GradientType=1 ); /* IE6-9 */
        }
    }
    .hero-copy--category {
        text-decoration: underline;
    }
    .hero-copy--title {
        margin-top: 1rem;
        font-weight: 800;
    }
    .hero-copy--summary {
        font-size: .75rem;
        font-weight: 600;
    }
    .main-hero {
        width: 50%;
        position: relative;
        z-index: 3;
    }
    .middle-hero {
        width: 50%;
        position: absolute;
        z-index: 2;
        left: 33%;
        top: 0;

        .hero-copy {
            width: 50%;
            position: absolute;
            bottom: 0;
            left: 45%;
            @include transform(translateX(-50%));

            .hero-copy--title, .hero-copy--summary {
                margin-top: 0;
            }
        }
    }

    .end-hero {
        width: 50%;
        position: absolute;
        z-index: 1;
        right: 0;
        top: 0;

        .hero-copy {
            width: 40%;
            position: absolute;
            bottom: 0;
            right: 0;

            .hero-copy--title, .hero-copy--summary {
                margin-top: 0;
            }
        }
    }
}

@mixin transition($value) {
  -webkit-transition: $value;
  -moz-transition: $value;
  -o-transition: $value;
  transition: $value;
}

@mixin transform($value) {
  -webkit-transform: $value;
  -moz-transform: $value;
  -o-transform: $value;
  transform: $value;
}
