    url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.woff2') format('woff2'),
    url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.woff') format('woff'),
    url('#{$root}/fonts/proxima-nova/proximanova-bold-webfont.svg#proxima-nova-bold-webfont') format('svg');
    font-weight: 700;
    font-style: normal;
}

*+ {
    p,
    pre,
    ul,
    ol,
    dl,
    blockquote {
        margin: $baseline * 0.5 0;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
        margin-top: $baseline * 1.5;
    }
}

h1,
h2,
h3,
h4,
h5,
h6 {
    +* {
        margin-top: $baseline * 0.5;
    }
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: 'Function Pro';
    font-weight: 700;
    line-height: 1;
    text-transform: uppercase;
}

h1 {
    font-size: $size-1;
}

h2 {
    font-size: $size-2;
}

h3 {
    font-size: $size-3;
}

h4 {
    font-size: $size-4;
}

h5 {
    font-size: $size-5;
}

h6 {
    font-size: $size-6;
}

ul,
ol {
    list-style-position: outside;
    margin-left: $baseline * 1.5;
}

ul {
    list-style-type: disc;
}

li+li {
    margin-top: $baseline * 0.25;
}

dd+dt {
    margin-top: $baseline * 0.25;
}

a {
    text-decoration: underline;
    text-decoration-skip: ink;
    text-decoration-color: rgba($link, 0.25);
    &:hover {
        text-decoration-color: rgba($link-hover, 0.25);
    }
    &:focus {
        text-decoration-color: rgba($link-focus, 0.25);
    }
    &:active {
        text-decoration-color: rgba($link-active, 0.25);
    }
}

.button {
    background: #FE1135;
    border: 2px solid transparent;
    color: #FFF;
    cursor: pointer;
    font-family: 'Function Pro', sans-serif;
    font-size: $size-5;
    font-weight: 700;
    line-height: 1;
    padding: 1rem 2rem;
    text-transform: uppercase;
    transition: background 0.2s, border-color 0.2s, color 0.2s;

    &:hover {
        background: transparent;
        border-color: #FE1135;
        color: #FE1135;
    }

    &:focus {
        background: transparent;
        border-color: #FE1135;
        color: #FE1135;
        text-decoration: underline;
        text-decoration-color: rgba(#FE1135, 0.25);
    }
}

.feature-quote {
    display: inline-block;
    text-align: center;

    .icons * {
        display: inline-block;
    }

    .icon-star {
        width: 1rem;
        path {
            fill: $color-torch-red;
        }
    }

    p {
        font-family: 'Function Pro', sans-serif;
        font-size: $size-2;
        font-style: italic;
        font-weight: 700;
        line-height: 1;
    }

    cite {
        font-family: 'Function Pro', sans-serif;
        font-size: $size-6;
        font-style: normal;
        line-height: 1;
        opacity: 0.75;
    }

    &.large {
        p {
            font-size: $size-2 * 2;
        }

        cite {
            font-size: $size-5 * 2;
        }

        .icon-star {
            width: 2rem;
        }
    }
}

.content-tile {
    $padding: 0.75rem;

    position: relative;

    &:before {
        display: block;
        content: '';
        width: 100%;
    }

    .content {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        padding: $padding;
    }

    .image {
        height: 0;
        padding-bottom: 2 / 3 * 100%;
        &:after {
            background: rgba(0, 0, 0, 0.5);
            content: "";
            display: block;
            position: absolute;
            top: $padding;
            bottom: $padding;
            left: $padding;
            right: $padding;
            opacity: 0;
            transition: opacity 0.2s;
        }
    }
    img {
        width: 100%;
    }

    .title {
        background: #fff;
        position: absolute;
        bottom: $padding;
        left: $padding;
        padding-top: 3px;
        padding-right: 6px;
        width: calc(70% - #{$padding});
    }
    h1 {
        font-size: $size-5;
        font-weight: 800;
        margin: 0;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
    }
    .subheading {
        overflow: hidden;
        text-transform: uppercase;
        font-size: $size-7;
    }

    .theme {
        background: grey;
        color: #fff;
        text-transform: uppercase;
        position: absolute;
        bottom: $padding;
        right: $padding;
        width: calc(30% - #{$padding});
        text-align: center;
        font-family: 'Function Pro';
        height: 15%;
        display: flex;
        flex-direction: column;
        justify-content: center;
    }

    blockquote {
        color: #fff;
        font-size: $size-7;
        margin: $padding;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
        display: -webkit-box;
        opacity: 0;
        transition: opacity 0.2s;
        cite {
            display: block;
            border-top: 1px solid rgba(255, 255, 255, 0.5);
            font-style: normal;
            text-transform: uppercase;
            margin-top: 5px;
            padding-top: 5px;
        }
    }

    button {
        background: gray;
        border: 0;
        color: #fff;
        width: 32px;
        height: 32px;
        margin-right: 1ex;
    }

    .icon {
        position: absolute;
        right: 0;
        bottom: calc(15% + 1.75rem);
        width: 30%;
        opacity: 0;
        transition: opacity 0.2s;

        &:after {
            border-top: 20px solid transparent;
            border-left: 30px solid #fff;
            border-bottom: 20px solid transparent;
            content: "";
            display: block;
            width: 0;
            height: 0;
            margin: 0 auto;
        }
    }

    a {
        color: $text;
        &:hover,
        &:focus {
            blockquote,
            .icon,
            .image:after {
                opacity: 1;
            }
        }
    }
}

